mapper.select:
select title,
addr1,
travel_info_id as travelInfoId,
first_image as firstImage
from travel_info
where sido_code = #{sidoCode}

    and gugun_code = #{gugunCode}


    and travel_info_id = #{travelInfoId}

order by readcount desc

select `like`, star
from travel_status
where travel_status_id = #{travelInfoId}
  and user_id = #{userId}

mapper.update:
update travel_status
set star = #{star}
where travel_status_id = #{travelStatusId}
